async(args) => {
    return new Promise(res => {
        const dom = document.createElement('iframe');
        dom.style.display = 'none';
        dom.src='https://doc.openugc.com/changelog/';
        dom.onload = () => {
            const data = dom.contentDocument.querySelector('main').innerText.trim();
            dom.remove();
            return res(data);
        };
        document.body.appendChild(dom);
    });
}

async (args) => {
  const { markdown } = args;
  if (markdown) {
    const data = await fetch('https://r.jina.ai/https://doc.openugc.com/getting-started/').then(r => r.text());
    return data;
  }
  return new Promise(res => {
    const dom = document.createElement('iframe');
    dom.style.display = 'none';
    dom.src = 'https://doc.openugc.com/getting-started/';
    dom.onload = () => {
      const data = dom.contentDocument.querySelector('main').innerText.trim();
      dom.remove();
      return res(data);
    };
    document.body.appendChild(dom);
  });
}
